-- select * from sys.sysusers
-- select * from sys.database_role_members

-- Connect to master
-- CREATE LOGIN loader WITH PASSWORD = 'Passw0rd!';

-- Connect to the SQL pool
CREATE USER loader FOR LOGIN loader;
GRANT ADMINISTER DATABASE BULK OPERATIONS TO loader;
GRANT INSERT TO loader;
GRANT SELECT TO loader;
GRANT CREATE TABLE TO loader;
GRANT ALTER ON SCHEMA::dbo TO loader;

CREATE WORKLOAD GROUP DataLoads
WITH ( 
    MIN_PERCENTAGE_RESOURCE = 100
    ,CAP_PERCENTAGE_RESOURCE = 100
    ,REQUEST_MIN_RESOURCE_GRANT_PERCENT = 100
    );

CREATE WORKLOAD CLASSIFIER [wgcELTLogin]
WITH (
        WORKLOAD_GROUP = 'DataLoads'
        ,MEMBERNAME = 'loader'
);


SELECT * FROM sys.workload_management_workload_groups
SELECT * FROM sys.workload_management_workload_classifiers
SELECT * FROM sys.workload_management_workload_classifier_details
SELECT * FROM sys.sysusers
